const allCandles = [];
let contentData = null;

for (const item of items) {
  // Collect candles if present
  if (item.json.allCandles !== undefined) {
    allCandles.push(...item.json.allCandles);
  }

  // Extract text content if present
  if (item.json.text && typeof item.json.text === "string") {
    contentData = item.json.text; // Store the text content
  }
}

return [{
  json: {
    allCandles, // Consolidated candle data
    content: contentData // Text content extracted
  }
}];

