const allData = $input.all();

const tweets = allData
	.map(item => item.json)
	.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));

if (!tweets.length) {
	return [{ $node: false, reason: "No tweets found" }];
}

return tweets.map(tweet => ({
	id: tweet.id,
	text: tweet.text,
	created_at: tweet.created_at,
	isNew: true
}));

