const input = items[0].json.text;

const matches = input.match(/\[(.*?) Tweet\]\n(.*?)(?=\n\[|$)/gs);

const output = matches.map(block => {
  const [_, label, text] = block.match(/\[(.*?) Tweet\]\n([\s\S]*)/);
  return {
    json: {
      strategy: label.trim(),
      tweet: text.trim()
    }
  };
});

return output;

const allData = $input.all();

const tweets = allData
	.map(item => item.json)
	.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));

if (!tweets.length) {
	return [{ $node: false, reason: "No tweets found" }];
}

return tweets.map(tweet => ({
	id: tweet.id,
	text: tweet.text,
	created_at: tweet.created_at,
	isNew: true
}));

