const inputText = $json["message"]["text"];

if (typeof inputText !== 'string') {
  throw new Error('Input Text must be a string');
}

const trimmedUpperText = inputText.trim().toUpperCase();

// Check if the input already contains a '/'
let tradingPairSymbol;

if (trimmedUpperText.includes('/')) {
  // If it already contains '/', use as is
  tradingPairSymbol = trimmedUpperText;
} else if (trimmedUpperText.length === 6) {
  // If it's a 6-character string like 'EURUSD', insert '/' between base and quote currencies
  tradingPairSymbol = trimmedUpperText.slice(0, 3) + '/' + trimmedUpperText.slice(3);
} else {
  // If the format is unexpected, throw an error
  throw new Error('Invalid trading pair format. Expected format like "EURUSD" or "EUR/USD".');
}

return [
  { json: { tradingPairSymbol } }
];

