const rssItems = items.json || items;

// Ensure there are items to process
if (!Array.isArray(rssItems) || rssItems.length === 0) {
  throw new Error('No RSS items found.');
}

// Sort items by publication date in descending order
rssItems.sort((a, b) => new Date(b.json.pubDate) - new Date(a.json.pubDate));

// Limit to the first 3 items
const latestItems = rssItems.slice(0, 7);

